mapper.update:
update qo_supplier_goods_stock
goods_price=#{goods_price}, goods_count=#{goods_count}, goods_sale_date=#{goods_sale_date}, modified_time=current_timestamp
where goods_id = #{goods_id} and id = #{id}

mapper.select:
SELECT qsi.supplier_id, qsgs.id, qsg.goods_id, qsg.goods_name, qsgs.goods_count, qsgs.goods_price, qsgs.goods_sale_date FROM qo_supplier_goods qsg right join qo_supplier_goods_stock qsgs on qsg.goods_id = qsgs.goods_id right join qo_supplier_info qsi on qsi.supplier_id = qsg.goods_supplier_id
qsg.goods_name like concat(#{goods_name},'%') and qsgs.goods_sale_date = #{goods_sale_date} and qsi.supplier_id = #{supplier_id}
LIMIT #{pageIndex},#{pageSize}
